mapper.select:
select distinct c.id company_id,c.title company_name,
date_format(#{time,jdbcType=VARCHAR},'%Y-%m') time,
ar.id restaurant_id,ar.name restaurant_name,ali.lng,ali.lat,ali.city_id,r.name city_name,ali.address ,case when awrd.restaurant_id is null then '0' else '1' end as support_takeout_of_food, case when sup_reserve.restaurant_id is null then '0' else '1' end support_reserve ,ar.cook_style,ar.consume,awrd.shipping_fee_min,awrd.polygon,awrd.shipping_dis from quancheng_db.16860_client c left join quancheng_db.16860_restaurant_client rc on c.id=rc.client_id left join quancheng_db.api_restaurants ar on ar.id=rc.restaurant_id left join (select restaurant_id from api_yuding group by restaurant_id) sup_reserve on sup_reserve.restaurant_id=ar.id left join (select awrd.restaurant_id,group_concat(awrc.name) as category_name ,awrd.shipping_fee_min,awrd.polygon,awrd.shipping_dis from quancheng_db.api_waimai_restaurant_detail awrd left join quancheng_db.api_waimai_restaurant_category awrc on awrd.category_id = awrc.id group by awrd.restaurant_id) awrd on awrd.restaurant_id=ar.id left join quancheng_db.api_lbs_infos ali on ar.lbs_id=ali.id inner join quancheng_db.16860_region r on r.id=ali.city_id where rc.deleted_at is null and ar.status=0 and ar.created_at <= #{time,jdbcType=VARCHAR} and ar.created_at <= #{endTime,jdbcType=VARCHAR} and ar.created_at >= #{startTime,jdbcType=VARCHAR}
and c.id= #{companyId,jdbcType=VARCHAR}
and c.id in ( ${companyIds,jdbcType=VARCHAR} )
and ali.city_id= #{cityId,jdbcType=INTEGER}
and r.name= #{cityName,jdbcType=VARCHAR}

mapper.sql:
    	select distinct c.id company_id,c.title company_name,
		
         date_format(#{time,jdbcType=VARCHAR},'%Y-%m') time,
        
		h.id hospital_id, h.name hospital_name,h.address,h.city city_id,r.name city_name,h.lng,h.lat
		from quancheng_db.16860_client c
		left join quancheng_db.16860_hospital_clients hc on c.id=hc.client_id
		left join  quancheng_db.16860_hospitals h on h.id=hc.hospital_id
		inner join quancheng_db.16860_region r on r.id=h.city
		where h.status='1'  
		  and hc.created_at <= #{time,jdbcType=VARCHAR} 
		  and hc.created_at <= #{endTime,jdbcType=VARCHAR} 
		  and hc.created_at >= #{startTime,jdbcType=VARCHAR} 
		 
		and c.id= #{companyId,jdbcType=VARCHAR} 
		
		 
		and c.id in ( ${companyIds,jdbcType=VARCHAR} ) 
		
		 
		and h.city = #{cityId,jdbcType=INTEGER} 
		
		 
		and r.name= #{cityName,jdbcType=VARCHAR}
