mapper.select:
select  id, time, company_id, company_name, region_id, region_name, restaurants_num, created_at, updated_at, deleted_at
from sj_company_restaurants_statistics


   id= #{id,jdbcType=INTEGER}  


  and  time = #{time,jdbcType=VARCHAR}  


   and company_id = #{companyId,jdbcType=INTEGER}  


   and company_name = #{companyName,jdbcType=VARCHAR}  


   and region_id = #{regionId,jdbcType=INTEGER}  


   and region_name = #{regionName,jdbcType=VARCHAR}  


   and restaurants_num = #{restaurantsNum,jdbcType=INTEGER}  


   and updated_at = #{updatedAt,jdbcType=TIMESTAMP}   


   and deleted_at = #{deletedAt,jdbcType=TIMESTAMP}
