mapper.select:
select   from sj_company_shelf_restaurant


   id= #{id,jdbcType=INTEGER}  


  and  time = #{time,jdbcType=VARCHAR}  


   and company_id = #{companyId,jdbcType=INTEGER}  


   and company_name = #{companyName,jdbcType=VARCHAR}

select sum(shelf_num) all_shelf_num from sj_company_shelf_restaurant


   id= #{id,jdbcType=INTEGER}  


  and  time = #{time,jdbcType=VARCHAR}  


   and company_id = #{companyId,jdbcType=INTEGER}  


   and company_name = #{companyName,jdbcType=VARCHAR}

mapper.delete:
    delete from  sj_company_shelf_restaurant where  company_id in (
		select t.company_id from (
			select company_id ,sum(shelf_num) allnum from sj_company_shelf_restaurant where 

			   time = #{time,jdbcType=VARCHAR}  

			 group by company_id
		) t where t.allnum=0 
	)
